  if( result == -1 ){
 	numOfX = numOfO = 0 ; 
	for( i = 0 ; i < size ; i++ ){ 
		if( board[i][size-i-1] == 1 ) {//这是一个表示副对角线的套路，记忆一下。
			numOfX ++ ;
		}else{
			numOfO ++ ;
		}
	}
	if( numOfO == size ){
		result = 0 ;
	} else{
